// import s from "./Style.module.css";
import "@fortawesome/fontawesome-free/css/all.css";

import s from "./Header.module.css";
import { ThemeContext } from "../context/ThemeContext";
import { useContext } from "react";

export default function Header() {
	const theme = useContext(ThemeContext);
	console.log(theme);
	return (
		<div className={s.headerContainer}>
			<div className={s.headerButtons}>
				<div className={s.darkButton}>
					<input type="checkbox" id="toggle" onChange={theme.toggleTheme} />
					<label htmlFor="toggle">DARK MODE</label>
				</div>
				<div className={s.langButton}>Turkeyeve gec</div>
			</div>
			<div className={s.headerBanner}>
				<div className={s.headerText}>
					<div className={s.headerTitle}>
						<p>
							<span>Hi!</span>
							<i className="fa-solid fa-hand-spock"></i>
						</p>
						<h2>
							I'm Dilay.
							<span className={s.nameCustom}></span>
							I'm a frontend developer. I can craft solid and scalable frontend
							products. <br /> Let's meet!{" "}
						</h2>
					</div>
					<div className={s.headerIcon}>
						<i className="fa-brands fa-linkedin"></i>
						<i className="fa-brands fa-github"></i>
					</div>
					<div className={s.headerFooter}>
						<p>Currently open for work for Web Design projects.</p>
						<p>
							Invite me to join your team{" "}
							<i class="fa-solid fa-arrow-right"></i> isik.kirisci@gmail.com
						</p>
					</div>
				</div>
				<div className={s.headerFoto}></div>
			</div>
		</div>
	);
}
